    public static void main(String[] args) {

        Runnable[] skibidi_arr = {Chill::printChill3, Chill::printChill4, Chill::printChill5, Chill::printChill4, Chill::printChill3, Chill::printChill6, Chill::printChill7, Chill::printChill6};
        // 자바는 콘솔창이 왜이뤱
        printChill();
        chill(1.3);

        // 두 번째 반복문
        for (int i = 0; i < 2; i++) {
            printChill2();
            chill(1.5);
            for (int j = 0; j < 5; j++) {
                for (Runnable sigma : skibidi_arr) {
                    clearLine(); //
                    sigma.run();
                    chill(0.05);
                }
            }
        }

        // 세 번째 반복문
        for (int i = 0; i < 9; i++) {
            for (Runnable sigma : skibidi_arr) {
                clearLine(); //
                sigma.run();
                chill(0.05);
            }
        }

        // 마지막 출력
        printChill2();
        chill(1);
        printChill();
        chill(5);


    }

    static void clearLine() {
        System.out.print("\r"); // 커서를 현재 줄의 맨 앞으로 이동
        System.out.print("                                    "); // 줄을 지운 후 공백으로 덮어씀
        System.out.print("\r"); // 다시 커서를 맨 앞으로 이동
    }


    static void chill(double seconds) {
        try {
            Thread.sleep((long)(seconds * 1000)); // 밀리초로 변환하여 시간 지연
        } catch (InterruptedException e) {
            e.printStackTrace();
        }
    }



